function recalcula()	{
	Inter=window.clearInterval(Inter);
	console.log(window.innerHeight);
	console.log(window.innerWidth);
	console.log('screen.width:'+screen.width+'\nscreen.height:'+screen.height+'\nwindow.innerWidth:'+window.innerWidth+'\nwindow.innerHeight:'+window.innerHeight+'\nwindow.outerWidth:'+window.outerWidth+'\nwindow.outerHeight:'+window.outerHeight+'\ndocument.documentElement.clientWidth:'+document.documentElement.clientWidth+'\ndocument.documentElement.clientHeight:'+document.documentElement.clientHeight+'\nwindow.devicePixelRatio:'+window.devicePixelRatio); 	
}
/*___ function popup(url, titulo, top, left, height, width) ______________________________________________________________________________________________________________________________________________________________________________________________________________________________	*/
function popup(url, titulo, top, left, height, width) {
	// $.ajax({url: 'tuit.php',type: 'POST',async:false,data: "clase=ingresar&tuit= ingresa a "+titulo,dataType: 'html', success: function(msg){ },error: function (xhr, ajaxOptions, thrownError) {alert("ERROR:" + xhr.responseText+" - "+thrownError);}});
	purl=url+"?";
	purl=purl.split("?")[0];
	obj = window.top.document.body;
	tab = document.createElement('div');
	tab.id = 'xxx' + num;
	obj.appendChild(tab);
	height = (height == 0 ? screen.height : height);
	width = (width == 0 ? screen.width-30 : width);
	window.top.document.getElementById("xxx" + num).style.position = "absolute";
	window.top.document.getElementById("xxx" + num).style.background = "#ccc";
	window.top.document.getElementById("xxx" + num).style.left = left + "%";
	window.top.document.getElementById("xxx" + num).style.top = top + "%";
	window.top.document.getElementById("xxx" + num).style.height = height+ "%";
	window.top.document.getElementById("xxx" + num).style.width = width + "%";
	window.top.document.getElementById("xxx" + num).setAttribute("oheight",height+ "%");
	window.top.document.getElementById("xxx" + num).setAttribute("owidth",width+ "%");
	window.top.document.getElementById("xxx" + num).style.borderRadius  = "0px 0px 5px 5px";
	window.top.document.getElementById("xxx" + num).style.overflow = "hidden";
	window.top.document.getElementById("xxx" + num).style.boxShadow = "2px 2px 5px black";
	window.top.document.getElementById("xxx" + num).style.zIndex = num + 1;
	window.top.document.getElementById("xxx" + num).onclick = function () { for (k = 0; k < num; k++) { if (window.top.document.getElementById("xxx" + k)) window.top.document.getElementById("xxx" + k).style.zIndex = 1000; }; this.style.zIndex = 1001; };
	window.top.document.getElementById("xxx" + num).ondblclick = function () { cual=$(this).attr("id").substring(3); minipopup(cual); }; 
	valor = "";
	valor = valor + "<div id='win_" + num + "' style='background:linear-gradient(to right, #ccc , #eee);font-family:calibri !important;font-size:18px !important;text-align:left;font-weight:bold !important;padding-top:0px;padding-left:0%;height:28px;overflow:hidden;' title='"+titulo+"'>" + titulo + "<div style='margin:0px;padding=0px;float:right;height:28px;width:56px;'><input type='button' class='w_botones'  style='height:28px;width:28px;font:bold 16px verdana;background:linear-gradient(#ddd, #666);color:#fff;' onclick='minipopup(" + num + ")' value='_' title='Minimizar'><input id='x_" + num + "'  class='w_botones' type='button' style='height:28px;width:28px;font:bold 16px verdana;background:linear-gradient(#ddd, #666);color:#fff;' onclick='cerrarpopup(" + num + ")' value='X' title='Cerrar' ></div></div>";
	valor = valor + "<div style='clear:both'></div><div><iframe name='ixxx" + num + "' id='ixxx" + num + "' src='" + url + "' height='"+(height*window.innerHeight/100)+"' width='100%' frameborder='0' style='background:#fff;padding:0px;margin:0px;'></iframe></div>";
	window.top.document.getElementById("xxx" + num).innerHTML = valor;
	$("#xxx" + num).resizable({start: function() {
		cual=$(this).attr("id").substring(3);	
		window.top.document.getElementById("xxx" + cual).style.backgroundColor="#aaa";
		window.top.document.getElementById("ixxx" + cual).style.display="none";
	}, stop: function() {
		cual=$(this).attr("id").substring(3);	
		window.top.document.getElementById("xxx" + cual).style.backgroundColor="#ccc";
		window.top.document.getElementById("ixxx" + cual).style.display="inline";
	} ,resize: function() {
		cual=$(this).attr("id").substring(3);	
		$(this).height(($(this).height()<30 ? 30 : $(this).height()) );
		$(this).width(($(this).width()<200 ? 200 : $(this).width()) );
		window.top.document.getElementById("xxx" + cual).height=$(this).height()-40;
		window.top.document.getElementById("xxx" + cual).width=$(this).width();
		window.top.document.getElementById("ixxx" + cual).height=$(this).height()-40;
		window.top.document.getElementById("ixxx" + cual).width=$(this).width();
		height=100*window.top.document.getElementById("xxx" + cual).height/window.innerHeight;
		width=100*window.top.document.getElementById("xxx" + cual).width/window.innerWidth;
		// console.log(window.top.document.getElementById("xxx" + cual).height,window.innerHeight,100*window.top.document.getElementById("xxx" + cual).height/window.innerHeight)
		window.top.document.getElementById("xxx" + cual).setAttribute("oheight",height+ "%");
		window.top.document.getElementById("xxx" + cual).setAttribute("owidth",width+ "%");
		cualid = window.top.document.getElementById("xxx"+cual);
	}} ).draggable({ stop: function (event, ui) {
			cual=$(this).attr("id").substring(3);
			if (ui.position.top < 0) { $("#xxx"+cual).animate({ top: '0' }, 100); };
			if (ui.position.left < 0) { $("#xxx"+cual).animate({ left: '0' }, 100);};
			for (k = 0; k < num; k++) { 
				if (window.top.document.getElementById("xxx" + k)) {
					window.top.document.getElementById("xxx" + k).style.zIndex = 1; 
				}
			}; 
			this.style.zIndex = 2; 
			cualid = window.top.document.getElementById("xxx"+cual);
		},iframeFix: true
    });	
	cualid = window.top.document.getElementById("xxx"+num);
   num++;
	$(".w_botones").mouseover(function() { $(this).css({"background":"linear-gradient(#aaa, #000)"}) }).mouseout(function() { $(this).css({"background":"linear-gradient(#ddd, #666)"}) }); 
   return (num-1);
}
/*___ function minipopup(cual)	 ______________________________________________________________________________________________________________________________________________________________________________________________________________________________	*/
function minipopup(cual)	{
	contenedor = "xxx"+cual;
	cualid = top.document.getElementById(contenedor);
	if(cualid.style.height=="28px")	{
		cualid.style.height=cualid.getAttribute("oheight")
		cualid.style.width=cualid.getAttribute("owidth")
	} else {
		cualid.style.height="28px";
		cualid.style.width="350px";
	};
}
/*___ function cerrarpopup(cual) ______________________________________________________________________________________________________________________________________________________________________________________________________________________________	*/
function cerrarpopup(cual) {    
	contenedor = "xxx"+cual;
	cualid = top.document.getElementById(contenedor);
	top.document.body.removeChild(cualid);
}
/*___ function procesa(cual)	{ ______________________________________________________________________________________________________________________________________________________________________________________________________________________________	*/
function procesamenu(cual)	{
	console.log(cual,cual.getAttribute("tamano"))
	programa=cual.getAttribute("id");
	titulo=cual.innerHTML;
	x=cual.getAttribute("tamano").split(",");
	popup(programa+".php",titulo,x[0],x[1],x[2],x[3])
}
</script>
